template<typename CharT, typename Traits>
friend std::basic_ostream<CharT, Traits> & operator << (
    std::basic_ostream<CharT, Traits> &builder, const OtpResendResponse &_writer)
{
    OtpResendResponse writer(
        _writer.m_buffer,
        _writer.m_offset,
        _writer.m_bufferLength,
        _writer.m_actingBlockLength,
        _writer.m_actingVersion);

    builder << '{';
    builder << R"("Name": "OtpResendResponse", )";
    builder << R"("sbeTemplateId": )";
    builder << writer.sbeTemplateId();
    builder << ", ";

    builder << R"("header": )";
    builder << writer.header();

    builder << ", ";
    builder << R"("otpExpiresIn": )";
    builder << +writer.otpExpiresIn();

    builder << ", ";
    builder << R"("remainingRetries": )";
    builder << +writer.remainingRetries();

    builder << '}';

    return builder;
}

